import React from "react";
import { useState } from "react";
import {useNavigate} from "react-router-dom"

const Signup = () => {

    
    const [name,setName]=useState("")
    const [email,setEmail]=useState("")
    const [password,setPassword]=useState("")
    const [cpassword,setconfPassword]=useState("")

  const navigate = useNavigate()

    const sigupData=async(e)=>{
        e.preventDefault()
console.log(name,email,password)
        const res= await fetch("/signup",{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,email,password
            })
        })
        const data = await res.json()
        if(res.status === 402 || !data){
            window.alert("invalid registeration")
            console.log("invalid registeration")
        }else{
            window.alert(" registeration succesfull")
            console.log(" registeration succesfull")

          //navigate("/login")
        }
    }
  return (
    <>
      <div className="flex flex-col max-w-md p-6 rounded-md sm:p-10 dark:bg-gray-900 dark:text-gray-100 ml-[500px] mt-[50px]">
        <div className="mb-8 text-center">
          <h1 className="my-3 text-4xl font-bold text-violet-700">Sign Up</h1>
          <p className="text-sm dark:text-gray-400">
            Sign up to access your account
          </p>
        </div>
        <form
          
         
          className="space-y-12 ng-untouched ng-pristine ng-valid"
          onSubmit={sigupData}

        >
          <div className="space-y-4">
          <div>
              <label for="email" className="block mb-2 text-sm">
                Name
              </label>
              <input
              value={name}
              onChange={(e)=>setName( e.target.value)}
                type="name"
                name="name"
                id="name"
                placeholder="xyz"
                className="w-full px-3 py-2 border rounded-md dark:border-gray-700 dark:bg-gray-900 dark:text-gray-100"
              />
            </div>
            <div>
              <label for="email" className="block mb-2 text-sm">
                Email address
              </label>
              <input
               value={email}
               onChange={(e)=>setEmail( e.target.value)}
                type="email"
                name="email"
                id="email"
                placeholder="leroy@jenkins.com"
                className="w-full px-3 py-2 border rounded-md dark:border-gray-700 dark:bg-gray-900 dark:text-gray-100"
              />
            </div>
            <div>
              <div className="flex justify-between mb-2">
                <label for="password" className="text-sm">
                  Password
                </label>
               
              </div>
              <input
               value={password}
               onChange={(e)=>setPassword( e.target.value)}
                type="password"
                name="password"
                id="password"
                placeholder="*****"
                className="w-full px-3 py-2 border rounded-md dark:border-gray-700 dark:bg-gray-900 dark:text-gray-100"
              />
            </div>
            <div>
              <div className="flex justify-between mb-2">
                <label for="password" className="text-sm">
                 confirm Password
                </label>
               
              </div>
              <input
               value={cpassword}
               onChange={(e)=>setconfPassword( e.target.value)}
                type="password"
                name="password"
                id="password"
                placeholder="*****"
                className="w-full px-3 py-2 border rounded-md dark:border-gray-700 dark:bg-gray-900 dark:text-gray-100"
              />
            </div>
          </div>
          <div className="space-y-2">
            <div>
              <button
                type="button"
                className="w-full px-8 py-3 font-semibold rounded-md dark:bg-violet-400 dark:text-gray-900 bg-violet-700 text-white"
              >
                Sign in
              </button>
            </div>
            
          </div>
        </form>
      </div>
    </>
  );
};

export default Signup;
