import React, { useState, useEffect } from "react";

const TodoList = () => {
  const [getTodo, setGetTodo] = useState([]);

  const getData = async () => {
    const res = await fetch("/gettodo", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    setGetTodo(data);
  };
  useEffect(() => {
    getData();
  }, [getTodo]);
  return (
    <>
      <div className="ml-[250px] mt-[100px]">
        <table className="table-fixed border-collapse border border-slate-400">
          <thead>
            <tr>
              <th className="border border-slate-300 p-4">TODO</th>
            </tr>
          </thead>

          <tbody>
            {getTodo.map((item) => (
              <tr>
                <td className="border border-slate-300 p-4">{item.data}</td>

                {/* <td className="border border-slate-300 p-4">
                <HiPencilAlt onClick={() => editValue(item)} />
              </td>
              <td className="border border-slate-300 p-4">
                <FaTrashAlt onClick={() => removeData(item)} />
              </td> */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default TodoList;
