import React, { useState, useEffect } from "react";
import axios from "axios"

const TodoList = () => {
  const [getTodo, setGetTodo] = useState([]);

  const fetchUserData = async () => {
    const resp = await axios.get("/gettodo");
    console.log(resp.data.data.getTodo.data);

    // if No users are there please dont set the values
   
      setGetTodo(resp.data.data.getTodo.data);
    
  };
  useEffect(() => {
    fetchUserData()
  }, [getTodo]);
  return (
    <>
      <div className="ml-[250px] mt-[100px]">
        <table className="table-fixed border-collapse border border-slate-400">
          <thead>
            <tr>
              <th className="border border-slate-300 p-4">TODO</th>
            </tr>
          </thead>

          <tbody>
            {getTodo.map((item) => (
              <tr>
                <td className="border border-slate-300 p-4">{item.data1}</td>

                {/* <td className="border border-slate-300 p-4">
                <HiPencilAlt onClick={() => editValue(item)} />
              </td>
              <td className="border border-slate-300 p-4">
                <FaTrashAlt onClick={() => removeData(item)} />
              </td> */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default TodoList;
